document.addEventListener("DOMContentLoaded",()=>{const e=document.querySelector(".petland-aside-menu-btn"),t=document.querySelector(".petland-aside-menu-btn-close");e?.addEventListener("click",()=>{document.body.classList.toggle("filter-menu-open"),e?.setAttribute("aria-expanded","true")}),t?.addEventListener("click",()=>{document.body.classList.toggle("filter-menu-open"),e?.setAttribute("aria-expanded","false")}),window.addEventListener("resize",()=>{document.body.classList.remove("filter-menu-open"),e?.setAttribute("aria-expanded","false")})});
