window.addEventListener("DOMContentLoaded",()=>{const s=document.querySelector(".site-header"),e=document.querySelector(".top-header-content");if(!s)return;const n=250,c=e?.offsetHeight?e?.offsetHeight:50;let o=!1,l=null;const r=()=>{const d=window.scrollY,t=o?d>n-c:d>n+c;t!==o&&(o=t,s.classList.toggle("scrolled",t),e&&!l&&e&&(l=setTimeout(()=>{e.classList.toggle("hidden",t),l=null},200)))};window.addEventListener("scroll",r,{passive:!0})});
